import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

const heading = "SUBSCRIBE"
const subtitle = "Sign up with your email address to receive news and updates."

const title = (
  <div className ="title">
    <h1>{heading}</h1>
  </div>
)
const subTitle = (
  <div className="subtitle">
    <h4>{subtitle}</h4>
  </div>
)


const form = (
  <div>
    <form>
    <input type='text' id='firstname' name="firstname" placeholder='First name'></input>
    </form>
  </div>
)

const jsxElement = (
  <div className = "box1">
  <div className = "box2">
    {title}
    {subTitle}
    {form}
    </div>
  </div>
)
const rootElement = document.getElementById('root')

ReactDOM.render(jsxElement, rootElement);
