import React,{useState} from "react";
import ReactDOM from "react-dom";
import "./index.css";
import twitterLogo from "./images/twitterlogo.png"
import { authentication } from "./firebase-config.js"
import { TwitterAuthProvider, signInWithPopup } from "firebase/auth";


const Title = (props) => {
  return (
    <div className="title">
      <h1>{props.heading}</h1>
    </div>
  )
};
const SubTitle = (props) => {
  return (
    <div className="subtitle">
      <h4>{props.subtitle}</h4>
    </div>
  )
};

const Form = ({
  data: { text
  },
}) => {

  const signInWithTwitter = () => {
    const provider = new TwitterAuthProvider();
    signInWithPopup(authentication, provider)
      .then((re) => {
        console.log(re)
      })
      .catch((err) => {
        console.log(err)
      });
  }

  // 

  // const [result, setResult] = useState("");
    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const handleChange = (e) => {
        const { className, value } = e.target;

        if (className === firstname) {
            setFirstname(value);
        }
        if (className === lastname) {
          setLastname(value);
      }
      if (className === email) {
        setLastname(value);
    }
        if (className === password) {
            setPassword(value);
        }
        if (className === confirmPassword) {
          setConfirmPassword(value);
      }

    }

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(`${firstname} ${lastname} you have successfully subscribed to my articles`)
    };


  return (
    <div className="form-container">
      <form className="form" action="" onSubmit={handleSubmit}>
        <div className="input-group">
        <input
          type={text}
          className="firstname"
          name="firstname"
          value={firstname} id="firstname"
          placeholder="First name" onChange={(e) => handleChange(e)}

        ></input>
        </div>

        <div className="input-group">
        <input
          type={text}
          className="lastname"
          name="lastname"
          value={lastname} id="lastname"
          placeholder="Last name" onChange={(e) => handleChange(e)}
        ></input>
        </div>
<div className="input-group">
        <input
          type="{text}"
          className="email"
          name="email"
          value={email} id="email"
          placeholder="Email" onChange={(e) => handleChange(e)}
        ></input>
        </div>

        <div className="input-group">
          <input 
          type="password"
           className="password" 
           name="password" 
           value={password} id="password" 
           placeholder="Password" onChange={(e) => handleChange(e)}></input> 
            </div>

        <div className="btn">
          <button type="button">Subscribe</button>
        </div>
        <div className="twitterIcon">
          <img src={twitterLogo} alt="twitter logo" onClick={signInWithTwitter}
            width="40"
            height="40" />
          <h3>Sign in with Twitter</h3>
        </div>
      </form>
    </div>
  )
};


const JsxElement = () => {
  const heading = "SUBSCRIBE";
  const subtitle = "Sign up with your email address to receive news and updates.";
  const data = {
    text: "text",
  }

  return (

    <div className="box1">
      <div className="box2">
        <Title heading={heading} />
        <SubTitle subtitle={subtitle} />
        <Form data={data} />
      </div>
    </div>
  )
};
const rootElement = document.getElementById("root");

ReactDOM.render(<JsxElement />, rootElement);
